package exo001;

public class Exo005_DisplayAmericanFlag {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * display following american flag
		 *  * * * * * * * ==================================
		 *   * * * * * *  ==================================
		 *  * * * * * * * ==================================
		 *   * * * * * *  ==================================
		 *  * * * * * * * ==================================
		 *   * * * * * *  ==================================
		 *  * * * * * * * ==================================
		 *   * * * * * *  ==================================
		 *  * * * * * * * ==================================
		 * =================================================
		 * =================================================
		 * =================================================
		 * =================================================
		 * =================================================
		 * =================================================
		 */

		for (int i = 1; i <= 15; i++) {
			String line = "";
			if (i < 10) { // display stars
				for (int j = 1; j < 8; j++) {
					if (j < 7) { // display stars
						if ((i % 2 == 1))
							line += "* ";
						else
							line += " *";
					} else {
						if ((i % 2 == 1))
							line += "* ==================================";
						else
							line += "  ==================================";
					}
				}
			} else
				line += "================================================";
			System.out.println(line);
		}
	}

}
